import { useWebsites } from '../../context/WebsitesContext';
import WebsiteCard from './WebsiteCard';

function GalleryGrid({ filteredWebsites = null }) {
  const { websites, loading, error } = useWebsites();
  const displayWebsites = filteredWebsites || websites;

  if (loading) return <div className="text-center py-10">Loading...</div>;
  if (error) return <div className="text-center py-10 text-red-500">Error: {error}</div>;
  if (displayWebsites.length === 0) return <div className="text-center py-10">No websites found</div>;

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 p-4">
      {displayWebsites.map(website => (
        <WebsiteCard key={website.id} website={website} />
      ))}
    </div>
  );
}
