1. Create a SubmitPage component (src/pages/SubmitPage.jsx):
```jsx
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useWebsites } from '../context/WebsitesContext';

function SubmitPage() {
  const navigate = useNavigate();
  const { addNewWebsite } = useWebsites();
  const [formData, setFormData] = useState({
    title: '',
    creator: '',
    description: '',
    thumbnailUrl: '',
    websiteUrl: '',
    techStack: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    // Clear error when field is edited
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: null }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = 'Title is required';
    if (!formData.creator.trim()) newErrors.creator = 'Creator name is required';
    if (!formData.description.trim()) newErrors.description = 'Description is required';
    if (!formData.thumbnailUrl.trim()) newErrors.thumbnailUrl = 'Thumbnail URL is required';
    else if (!/^https?:\/\/.+/.test(formData.thumbnailUrl)) {
      newErrors.thumbnailUrl = 'Please enter a valid URL starting with http:// or https://';
    }
    if (!formData.websiteUrl.trim()) newErrors.websiteUrl = 'Website URL is required';
    else if (!/^https?:\/\/.+/.test(formData.websiteUrl)) {
      newErrors.websiteUrl = 'Please enter a valid URL starting with http:// or https://';
    }
    if (!formData.techStack.trim()) newErrors.techStack = 'Tech stack is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsSubmitting(true);

    // Process tech stack from comma-separated string to array
    const techStackArray = formData.techStack.split(',').map(tech => tech.trim());

    const newWebsite = {
      ...formData,
      techStack: techStackArray,
      id: Date.now().toString(), // Simple ID generation
      createdAt: new Date().toISOString()
    };

    try {
      addNewWebsite(newWebsite);
      navigate('/');
    } catch (error) {
      setErrors({ submit: error.message });
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <h1 className="text-2xl font-bold mb-6">Submit Your Website</h1>

      {errors.submit && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {errors.submit}
        </div>
      )}

      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded-lg p-6">
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
            Website Title *
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.title ? 'border-red-500' : 'border-gray-300'}`}
            placeholder="My Awesome Website"
          />
          {errors.title && <p className="text-red-500 text-xs mt-1">{errors.title}</p>}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="creator">
            Your Name *
          </label>
          <input
            type="text"
            id="creator"
            name="creator"
            value={formData.creator}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.creator ? 'border-red-500' : 'border-gray-300'}`}
            placeholder="John Doe"
          />
          {errors.creator && <p className="text-red-500 text-xs mt-1">{errors.creator}</p>}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
            Description *
          </label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.description ? 'border-red-500' : 'border-gray-300'}`}
            rows="4"
            placeholder="Describe your website..."
          ></textarea>
          {errors.description && <p className="text-red-500 text-xs mt-1">{errors.description}</p>}
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="thumbnailUrl">
            Thumbnail URL *
          </label>
          <input
            type="text"
            id="thumbnailUrl"
            name="thumbnailUrl"
            value={formData.thumbnailUrl}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.thumbnailUrl ? 'border-red-500' : 'border-gray-300'}`}
            placeholder="https://example.com/thumbnail.jpg"
          />
          {errors.thumbnailUrl && <p className="text-red-500 text-xs mt-1">{errors.thumbnailUrl}</p>}
          <p className="text-gray-500 text-xs mt-1">Direct link to an image that represents your website</p>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="websiteUrl">
            Website URL *
          </label>
          <input
            type="text"
            id="websiteUrl"
            name="websiteUrl"
            value={formData.websiteUrl}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.websiteUrl ? 'border-red-500' : 'border-gray-300'}`}
            placeholder="https://mywebsite.com"
          />
          {errors.websiteUrl && <p className="text-red-500 text-xs mt-1">{errors.websiteUrl}</p>}
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="techStack">
            Tech Stack *
          </label>
          <input
            type="text"
            id="techStack"
            name="techStack"
            value={formData.techStack}
            onChange={handleChange}
            className={`w-full px-3 py-2 border rounded-lg ${errors.techStack ? 'border-red-500' : 'border-gray-300'}`}
            placeholder="React, TailwindCSS, Firebase"
          />
          {errors.techStack && <p className="text-red-500 text-xs mt-1">{errors.techStack}</p>}
          <p className="text-gray-500 text-xs mt-1">Comma-separated list of technologies used</p>
        </div>

        <div className="flex items-center justify-end">
          <button
            type="submit"
            disabled={isSubmitting}
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? 'Submitting...' : 'Submit Website'}
          </button>
        </div>
      </form>
    </div>
  );
}
```
